import React, { useState, useCallback } from "react";
import Header from "./components/Header";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";
import ScheduleTable from "./components/ScheduleTable";
import Footer from "./components/Footer";
import HelpPopup from "./components/HelpPopup"; // Import HelpPopup
import { useCourses, useSchedules } from "./useCourses";
import { stringToColor } from "./utils";
import Dropdown from "./components/Dropdown"; // Import Dropdown

function App() {
    const [allSchedules, setAllSchedules] = useState([]);
    const [currentSchedule, setCurrentSchedule] = useState(0);
    const [min, setMin] = useState(2);
    const [max, setMax] = useState(4);
    const [term, setTerm] = useState("2024");
    const [quarter, setQuarter] = useState("Winter");

    const {
        courses,
        forceList,
        errorMessage,
        setErrorMessage,
        handleAddCourse,
        handleRemoveCourse,
        handleToggleForce,
    } = useCourses();

    const sortSchedules = (schedules) => {
        if (schedules.length <= 0) return;

        const sortValue = document.getElementById("scheduleSort").value;

        schedules.sort((a, b) => {
            if (sortValue === "score") {
                return b.score - a.score;
            } else if (sortValue === "end") {
                return b.weights.end - a.weights.end;
            } else if (sortValue === "gap") {
                return b.weights.gap - a.weights.gap;
            } else if (sortValue === "gpa") {
                return b.weights.gpa - a.weights.gpa;
            } else if (sortValue === "start") {
                return b.weights.start - a.weights.start;
            }
            return 0;
        });
    };

    const clearSchedule = useCallback(() => {
        const table = document.getElementById("calendar");
        const elements = table.getElementsByTagName("*");

        for (let i = 0; i < elements.length; i++) {
            const element = elements[i];
            if (element.id) {
                element.textContent = "";
                element.style = "";
                element.classList.remove("scheduled-course");
            }
        }
    }, []);

    const addCoursesToCalendar = useCallback(
        (schedule) => {
            const courses = schedule.courses;
            const cornerCell = document.getElementById("cornerCell");
            cornerCell.textContent = `Schedule ${currentSchedule}`;

            courses.forEach((course) => {
                const days = course.days.split("");
                const startTime = parseInt(course.start_time);
                const endTime = parseInt(course.end_time);

                days.forEach((day) => {
                    let startHour = Math.floor(startTime / 100);
                    let endHour = Math.ceil(endTime / 100);
                    for (let i = startHour; i < endHour; i++) {
                        if (i < 10) {
                            i = `0${i}`;
                        }
                        const cellId = `${day}-${i}00`;
                        const cell = document.getElementById(cellId);
                        const bgColor = stringToColor(course.crn);

                        cell.style.backgroundColor = bgColor;
                        cell.innerHTML = `<b>${course.subject}</b><br>${course.instructor}<br>${course.crn}<br>${course.room}`;
                        cell.classList.add("scheduled-course");
                    }
                });

                if (course.lab_days) {
                    const labDays = course.lab_days.split("");
                    const labStart = parseInt(course.lab_start_time);
                    const labEnd = parseInt(course.lab_end_time);
                    labDays.forEach((labDay) => {
                        let labStartHour = Math.floor(labStart / 100);
                        let labEndHour = Math.ceil(labEnd / 100);
                        for (let i = labStartHour; i < labEndHour; i++) {
                            if (i < 10) {
                                i = `0${i}`;
                            }
                            const cellId = `${labDay}-${i}00`;
                            const cell = document.getElementById(cellId);
                            const bgColor = stringToColor(course.crn);

                            cell.style.backgroundColor = bgColor;
                            cell.innerHTML = `<b>${course.subject} LAB</b><br>${course.instructor}<br>${course.crn}<br>${course.lab_room}`;
                            cell.classList.add("scheduled-course");
                        }
                    });
                }
            });
        },
        [currentSchedule]
    );

    const displaySchedule = useCallback(
        (schedule) => {
            if (!schedule || !schedule.courses) {
                console.error("Invalid schedule:", schedule);
                return;
            }

            clearSchedule();
            addCoursesToCalendar(schedule);
        },
        [clearSchedule, addCoursesToCalendar]
    );

    const { generateJSON } = useSchedules(
        setAllSchedules,
        setCurrentSchedule,
        displaySchedule,
        currentSchedule,
        setErrorMessage,
        sortSchedules
    );

    return (
        <div className="App bg-white">
            <Header />
            <div className="container mx-auto px-4 py-6 bg-white text-darkgray">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="bg-white p-4 rounded-lg shadow-md">
                        <CourseForm onAddCourse={handleAddCourse} />
                        <CourseList
                            courses={courses}
                            onRemoveCourse={handleRemoveCourse}
                            onToggleForce={handleToggleForce}
                        />
                        <button
                            className="mt-4 btn btn-primary bg-darkblue text-white"
                            onClick={() =>
                                generateJSON(
                                    courses,
                                    forceList,
                                    min,
                                    max,
                                    term,
                                    quarter
                                )
                            }>
                            Submit
                        </button>
                        {errorMessage && (
                            <div
                                id="errorMessage"
                                className="mt-4 text-red-500">
                                {errorMessage}
                            </div>
                        )}
                    </div>
                    <div>
                        <Dropdown
                            label="Minimum Courses:"
                            value={min}
                            options={[...Array(6).keys()].map((i) => i + 1)}
                            onChange={(e) => setMin(e.target.value)}
                            className="w-48"
                        />
                        <Dropdown
                            label="Maximum Courses:"
                            value={max}
                            options={[...Array(10).keys()].map((i) => i + 1)}
                            onChange={(e) => setMax(e.target.value)}
                            className="w-48"
                        />
                        <Dropdown
                            label="Year:"
                            value={term}
                            options={[2024, 2025]}
                            onChange={(e) => setTerm(e.target.value)}
                            className="w-48"
                        />
                        <Dropdown
                            label="Quarter:"
                            value={quarter}
                            options={["Winter", "Spring", "Summer", "Fall"]}
                            onChange={(e) => setQuarter(e.target.value)}
                            className="w-48"
                        />
                        <ScheduleTable
                            schedule={allSchedules}
                            currentSchedule={currentSchedule}
                            displaySchedule={displaySchedule}
                            clearSchedule={clearSchedule}
                        />
                    </div>
                </div>
            </div>
            <Footer />
            <div
                id="backdrop"
                className="hidden fixed inset-0 bg-gray-600 bg-opacity-50 z-40"></div>
            <div
                id="help-popup"
                className="hidden fixed inset-0 flex justify-center items-center z-50">
                <HelpPopup />
            </div>
        </div>
    );
}

export default App;
