import React, { useState, useEffect } from "react";
import Dropdown from "./Dropdown";

const CourseForm = ({ onAddCourse }) => {
    const [courseName, setCourseName] = useState("");
    const [section, setSection] = useState("");
    const [subjects, setSubjects] = useState([]);
    const [error, setError] = useState("");

    useEffect(() => {
        fetch("/subjects.txt")
            .then((response) => response.text())
            .then((text) => {
                const courses = text.trim().split("\n");
                setSubjects(courses);
                setCourseName(courses[0]); // Set the default value to the first subject
            })
            .catch((error) => {
                console.error("Error fetching subjects:", error);
            });
    }, []);

    const handleAddCourse = () => {
        if (!section) {
            setError("Course number is required");
            return;
        }
        setError("");
        if (courseName && onAddCourse(courseName, section)) {
            setCourseName(subjects[0]);
            setSection("");
        }
    };

    const handleSectionChange = (e) => {
        const value = e.target.value;
        if (/^\d*$/.test(value)) {
            setSection(value);
        }
    };

    return (
        <div className="pl-4 flex-col">
            <Dropdown
                label="Select Course"
                value={courseName}
                options={subjects}
                onChange={(e) => setCourseName(e.target.value)}
                className="w-36"
            />
            <input
                type="text"
                value={section}
                placeholder="Course Number"
                onChange={handleSectionChange}
                className="input input-bordered mt-2 w-36 max-w-xs bg-white border-lightgray ml-2"
            />
            <button
                className="btn btn-primary mt-2 bg-darkblue text-white ml-2"
                onClick={handleAddCourse}>
                Add Course
            </button>
            {error && <div className="mt-2 text-red-500">{error}</div>}
        </div>
    );
};

export default CourseForm;
