// utils.js

export const stringToColor = (str) => {
    let hash = 0;
    for (let i = 0; i < str.length; i++) {
        hash = str.charCodeAt(i) + ((hash << 5) - hash);
    }
    const c = (hash & 0x00ffffff).toString(16).toUpperCase().padStart(6, "0");
    return `#${c}`;
};

export const getQuarterCode = (quarter) => {
    switch (quarter) {
        case "Winter":
            return 10;
        case "Spring":
            return 20;
        case "Fall":
            return 30;
        case "Summer":
            return 40;
        default:
            return 0;
    }
};
