import React, { useState } from "react";
import Header from "./components/Header";
import CourseForm from "./components/CourseForm";
import CourseList from "./components/CourseList";
import ScheduleTable from "./components/ScheduleTable";
import Footer from "./components/Footer";

function App() {
    const [courses, setCourses] = useState([]);
    const [forceList, setForceList] = useState([]);
    const [schedule, setSchedule] = useState([]);
    const [allSchedules, setAllSchedules] = useState([]);
    const [currentSchedule, setCurrentSchedule] = useState(0);

    const handleAddCourse = (courseName, section) => {
        if (
            courses.some((course) => course.name === `${courseName} ${section}`)
        ) {
            return false;
        }

        const newCourse = {
            name: `${courseName} ${section}`,
            forced: false,
        };

        setCourses([...courses, newCourse]);
        return true;
    };

    const handleRemoveCourse = (courseName) => {
        setCourses(courses.filter((course) => course.name !== courseName));
        setForceList(forceList.filter((course) => course !== courseName));
    };

    const handleToggleForce = (courseName) => {
        setCourses(
            courses.map((course) =>
                course.name === courseName
                    ? { ...course, forced: !course.forced }
                    : course
            )
        );
        if (forceList.includes(courseName)) {
            setForceList(forceList.filter((course) => course !== courseName));
        } else {
            setForceList([...forceList, courseName]);
        }
    };

    const generateJSON = () => {
        const min = minSelect.value; // Get min value
        const max = maxSelect.value; // Get max value
        const term = termSelect.value; // Get term value
        const quarter = quarterSelect.value; // Get quarter valueif (className && section && min && max && term && quarter) { // Check if all values are provided
        if (courses.length < min) {
            errorMessage.innerHTML +=
                "Cannot have less courses than minimum courses in a schedule.";
            return;
        }

        if (forceList.length > max) {
            errorMessage.innerHTML +=
                "Cannot have more forced courses than maximum courses in a schedule.";
            return;
        }

        const scheduleinfo = {
            courses: courses,
            force: forceList,
            min: min,
            max: max,
            term: term + quarter,
        };

        schedule = scheduleinfo;
        const json = JSON.stringify(schedule); // Convert classes array to JSON string
        console.log(json); // Output JSON string to the console

        fetch("/schedule-optimizer", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: json,
        })
            .then((response) => response.json())
            .then((response) => {
                // Display the response in the console
                console.log(response); // Here is the response
                if (response["errors"].length > 0) {
                    const errorMessage =
                        document.getElementById("errorMessage");
                    errorMessage.innerHTML = response["errors"];
                    if (response["warnings"].length > 0) {
                        const errorMessage =
                            document.getElementById("errorMessage");
                        errorMessage.innerHTML +=
                            "<br>" + response["warnings"].join("<br>");
                    }
                } else {
                    if (response["warnings"].length > 0) {
                        const errorMessage =
                            document.getElementById("errorMessage");
                        errorMessage.innerHTML +=
                            "<br>" + response["warnings"].join("<br>");
                    }
                    all_schedules = response["schedules"];
                    sortSchedules(); // Update the sort based on the current value
                    current_schedule = 0;
                    displaySchedule(all_schedules[current_schedule]);
                }
            })
            .catch((error) => {
                // Handle any errors
                console.error("Error:", error);
            });
    };

    return (
        <div className="App">
            <Header />
            <div className="container">
                <CourseForm onAddCourse={handleAddCourse} />
                <CourseList
                    courses={courses}
                    onRemoveCourse={handleRemoveCourse}
                    onToggleForce={handleToggleForce}
                />
                <button onClick={generateJSON}>Submit</button>
                <ScheduleTable
                    schedule={schedule}
                    currentSchedule={currentSchedule}
                    displaySchedule={addCoursesToCalendar}
                    clearSchedule={clearSchedule}
                />
            </div>
            <Footer />
        </div>
    );
}

export default App;
