// ClassDetailsPopup.js
import React from "react";

const ClassDetailsPopup = ({ course, onClose }) => {
    const renderValue = (key, value) => {
        if (Array.isArray(value)) {
            return (
                <ul className="list-disc pl-4 text-xs">
                    {value.map((item, index) => (
                        <li key={index} className="mb-1">
                            {Object.entries(item).map(([subKey, subValue]) => (
                                <div key={subKey} className="text-xs">
                                    <strong>{subKey}:</strong>{" "}
                                    {subValue.toString()}
                                </div>
                            ))}
                        </li>
                    ))}
                </ul>
            );
        } else if (typeof value === "object" && value !== null) {
            return <span className="text-xs">{JSON.stringify(value)}</span>;
        } else {
            return <span className="text-xs">{value.toString()}</span>;
        }
    };

    return (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
            <div className="relative top-20 mx-auto p-4 border w-80 shadow-lg rounded-md bg-white">
                <div className="mt-2 text-center">
                    <h3 className="text-lg leading-6 font-medium text-gray-900 pb-2 border-b border-gray-300">
                        {course.Subject}
                    </h3>
                    <button
                        onClick={onClose}
                        className="absolute top-0 right-0 mt-1 mr-2 text-gray-400 hover:text-gray-600 text-xl">
                        &times;
                    </button>
                    <div className="px-4 py-2 text-left max-h-96 overflow-y-auto">
                        {Object.entries(course).map(([key, value]) => (
                            <div key={key} className="mb-1">
                                <strong className="text-xs">{key}:</strong>
                                <div className="ml-1 text-xs">
                                    {renderValue(key, value)}
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ClassDetailsPopup;
