import React, { useState, useEffect, useRef } from "react";
import { DayPilot, DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import "./CalendarStyles.css"; // Customize this CSS as needed

const Calendar = ({ schedule }) => {
    const calendarRef = useRef();

    useEffect(() => {
        const events = schedule.courses.map((course) => ({
            id: course.crn,
            text: `${course.subject} (${course.instructor})`,
            start: `2023-10-02T${course.start_time}`,
            end: `2023-10-02T${course.end_time}`,
            backColor: "#6aa84f", // You can customize the color based on your requirement
        }));

        const startDate = "2023-10-02"; // You can dynamically set the start date as needed

        calendarRef.current.control.update({ startDate, events });
    }, [schedule]);

    return (
        <div>
            <DayPilotCalendar
                {...{
                    viewType: "Week",
                    durationBarVisible: false,
                }}
                ref={calendarRef}
            />
        </div>
    );
};

export default Calendar;
