import React, { useEffect, useState } from "react";

const ScheduleTable = ({ schedule, currentSchedule, displaySchedule }) => {
    console.log("ScheduleTable rendering", { schedule, currentSchedule });
    const [tableCreated, setTableCreated] = useState(false);

    useEffect(() => {
        createTable();
        setTableCreated(true);
    }, []);

    useEffect(() => {
        if (tableCreated && schedule) {
            displaySchedule(schedule);
        }
    }, [schedule, displaySchedule, tableCreated]);

    const createTable = () => {
        console.log("Creating table");
        const hours = [
            "0800",
            "0900",
            "1000",
            "1100",
            "1200",
            "1300",
            "1400",
            "1500",
            "1600",
            "1700",
        ];
        const fullDaysOfWeek = [
            "Monday",
            "Tuesday",
            "Wednesday",
            "Thursday",
            "Friday",
        ];

        const calendarDiv = document.getElementById("calendar");
        if (calendarDiv) {
            calendarDiv.innerHTML = ""; // Clear existing content
            const table = document.createElement("table");
            table.id = "calendar-table";
            table.className = "w-full border-collapse";

            const headerRow = table.insertRow();
            headerRow.className = "bg-gray-100";
            const cornerCell = headerRow.insertCell();
            cornerCell.id = "cornerCell";
            cornerCell.className =
                "border border-gray-300 p-2 text-center font-bold";

            fullDaysOfWeek.forEach((day) => {
                const headerCell = headerRow.insertCell();
                headerCell.textContent = day;
                headerCell.className =
                    "border border-gray-300 p-2 text-center font-bold";
            });

            hours.forEach((hour) => {
                const row = table.insertRow();
                const hourCell = row.insertCell();
                hourCell.textContent = `${hour.slice(0, 2)}:${hour.slice(2)}`;
                hourCell.className = "border border-gray-300 p-2 text-center";

                fullDaysOfWeek.forEach((day) => {
                    const cell = row.insertCell();
                    cell.id = `${day}-${hour}`;
                    cell.className = "border border-gray-300 p-2 text-center";
                });
            });

            calendarDiv.appendChild(table);
        }
    };

    return (
        <div id="calendar" className="w-full h-full overflow-x-auto">
            {!tableCreated && <p>Loading calendar...</p>}
        </div>
    );
};

export default ScheduleTable;
