import React, { useState } from "react";

function CourseForm({ onAddCourse, onSubmit }) {
    const [courseName, setCourseName] = useState("");
    const [sectionNumber, setSectionNumber] = useState("");
    const [quarter, setQuarter] = useState("");
    const [minCourses, setMinCourses] = useState(2);
    const [maxCourses, setMaxCourses] = useState(4);
    const [year, setYear] = useState(2024);

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddCourse(courseName, sectionNumber);
        setCourseName("");
        setSectionNumber("");
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        onSubmit({
            courseName,
            sectionNumber,
            quarter,
            minCourses,
            maxCourses,
            year,
        });
    };

    const quarterOptions = [
        { label: "Winter", value: 10 },
        { label: "Spring", value: 20 },
        { label: "Summer", value: 30 },
        { label: "Fall", value: 40 },
    ];

    return (
        <div>
            <form onSubmit={handleFormSubmit}>
                <select
                    id="courseSelect"
                    value={courseName}
                    onChange={(e) => setCourseName(e.target.value)}>
                    <option value="">Select Course</option>
                    {/* Add options here */}
                </select>
                <input
                    id="sectionNumber"
                    type="text"
                    value={sectionNumber}
                    onChange={(e) => setSectionNumber(e.target.value)}
                    placeholder="Course Number"
                />
                <div>
                    <label htmlFor="minCourses">Minimum Courses:</label>
                    <select
                        id="minCourses"
                        value={minCourses}
                        onChange={(e) => setMinCourses(e.target.value)}>
                        {[...Array(10).keys()].map((i) => (
                            <option key={i + 1} value={i + 1}>
                                {i + 1}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label htmlFor="maxCourses">Maximum Courses:</label>
                    <select
                        id="maxCourses"
                        value={maxCourses}
                        onChange={(e) => setMaxCourses(e.target.value)}>
                        {[...Array(10).keys()].map((i) => (
                            <option key={i + 1} value={i + 1}>
                                {i + 1}
                            </option>
                        ))}
                    </select>
                </div>
                <div>
                    <label htmlFor="yearSelect">Year:</label>
                    <input
                        id="yearSelect"
                        type="number"
                        value={year}
                        onChange={(e) => setYear(e.target.value)}
                    />
                </div>
                <div>
                    <label htmlFor="quarterSelect">Quarter:</label>
                    <select
                        id="quarterSelect"
                        value={quarter}
                        onChange={(e) => setQuarter(e.target.value)}>
                        {quarterOptions.map((qtr) => (
                            <option key={qtr.value} value={qtr.value}>
                                {qtr.label}
                            </option>
                        ))}
                    </select>
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default CourseForm;
