import React, { useState, useCallback } from "react";
import CourseForm from "./CourseForm";
import CourseList from "./CourseList";
import ScheduleTable from "./ScheduleTable";

function App() {
    const [courses, setCourses] = useState([]);
    const [forceList, setForceList] = useState([]);
    const [allSchedules, setAllSchedules] = useState([]);
    const [currentSchedule, setCurrentSchedule] = useState(0);
    const [minCourses, setMinCourses] = useState(2);
    const [maxCourses, setMaxCourses] = useState(4);
    const [year, setYear] = useState(2024);
    const [quarter, setQuarter] = useState(10); // default to Winter

    const addCourse = (courseName, sectionNumber) => {
        setCourses([...courses, { courseName, sectionNumber }]);
    };

    const handleFormSubmit = ({
        courseName,
        sectionNumber,
        quarter,
        minCourses,
        maxCourses,
        year,
    }) => {
        if (courses.length < minCourses) {
            alert(
                "Cannot have less courses than minimum courses in a schedule."
            );
            return;
        }

        if (forceList.length > maxCourses) {
            alert(
                "Cannot have more forced courses than maximum courses in a schedule."
            );
            return;
        }

        const scheduleInfo = {
            courses,
            force: forceList,
            min: minCourses,
            max: maxCourses,
            term: `${year}${quarter}`,
        };

        const json = JSON.stringify(scheduleInfo);
        console.log(json);

        fetch("/schedule-optimizer", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: json,
        })
            .then((response) => response.json())
            .then((response) => {
                console.log(response);
                if (response.errors.length > 0) {
                    alert(response.errors.join("\n"));
                } else {
                    if (response.warnings.length > 0) {
                        alert(response.warnings.join("\n"));
                    }
                    setAllSchedules(response.schedules);
                    setCurrentSchedule(0);
                }
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    };

    const displaySchedule = useCallback((schedule) => {
        // Your display schedule logic here
    }, []);

    return (
        <div>
            <header className="navbar">
                <a href="/" className="logo">
                    WWU Schedule Optimizer
                </a>
                <button className="help-button">Help</button>
            </header>
            <div className="container">
                <div className="left-container">
                    <CourseForm
                        onAddCourse={addCourse}
                        onSubmit={handleFormSubmit}
                    />
                </div>
                <div className="right-container">
                    <ScheduleTable
                        schedules={allSchedules}
                        displaySchedule={displaySchedule}
                    />
                </div>
            </div>
            <footer>
                <div className="footer-content">
                    <p>
                        This is a schedule optimizer passion project for Western
                        Washington University.
                    </p>
                    <p>
                        Created by Cooper Morgan, Konnor Kooi, Robert Bates,
                        Arne Wiseman, and Ben Huynh.
                    </p>
                    <p>
                        <a href="https://github.com/your-repo-link">
                            GitHub Source Code
                        </a>
                    </p>
                    <p>
                        Report Issues to{" "}
                        <a href="mailto:admin@cwooper.me">admin@cwooper.me</a>
                    </p>
                    <p>
                        Disclaimer: This project is not affiliated with Western
                        Washington University. It is an independent initiative
                        developed solely for educational and personal use. All
                        data provided by this project is for informational
                        purposes only and should not be considered official or
                        binding. Use at your own discretion.
                    </p>
                </div>
            </footer>
        </div>
    );
}

export default App;
