import React, { useRef, useEffect, useState } from "react";
import { DayPilot, DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import "./CalendarStyles.css";

const Calendar = ({ schedules }) => {
    const calendarRef = useRef();

    useEffect(() => {
        if (!calendarRef.current) return;

        const dp = calendarRef.current.control;

        // Update the calendar when schedules change
        if (schedules && schedules.length > 0) {
            const events = schedules.flatMap((schedule, index) =>
                (schedule.courses || []).map((course) => ({
                    id: course.crn,
                    text: `${course.subject} (${course.instructor})`,
                    start: `2023-10-02T${course.start_time.substring(
                        0,
                        2
                    )}:${course.start_time.substring(2, 4)}`,
                    end: `2023-10-02T${course.end_time.substring(
                        0,
                        2
                    )}:${course.end_time.substring(2, 4)}`,
                    backColor: "#6aa84f",
                    scheduleIndex: index,
                }))
            );

            dp.events.list = events;
            dp.update();
        } else {
            dp.clearSelection();
            dp.events.list = [];
            dp.update();
        }
    }, [schedules]);

    const calendarConfig = {
        viewType: "Week",
        businessBeginsHour: 8,
        businessEndsHour: 17,
        showNonBusiness: false,
        cellDuration: 60,
        days: 5,
        durationBarVisible: false,
        timeRangeSelectedHandling: "Enabled",
        eventDeleteHandling: "Update",
        onEventDelete: async (args) => {
            const dp = calendarRef.current.control;
            dp.events.remove(args.e);
        },
    };

    return (
        <div className="w-full">
            <DayPilotCalendar {...calendarConfig} ref={calendarRef} />
        </div>
    );
};

export default Calendar;
