import React, { useState, useEffect } from "react";

const CourseForm = ({ onAddCourse }) => {
    const [courses, setCourses] = useState([]);
    const [courseName, setCourseName] = useState("");
    const [sectionNumber, setSectionNumber] = useState("");
    const [errorMessage, setErrorMessage] = useState("");

    useEffect(() => {
        fetch("subjects.txt")
            .then((response) => response.text())
            .then((text) => {
                const courses = text.trim().split("\n");
                setCourses(courses);
            })
            .catch((error) => {
                console.error("Error fetching subjects:", error);
            });
    }, []);

    const handleAddCourse = () => {
        if (!courseName || !sectionNumber) {
            setErrorMessage("Ensure you have less than 10 classes added.");
            return;
        }

        if (onAddCourse(courseName, sectionNumber)) {
            setCourseName("");
            setSectionNumber("");
            setErrorMessage("");
        } else {
            setErrorMessage("Duplicate or invalid course.");
        }
    };

    return (
        <div>
            <select
                id="courseSelect"
                value={courseName}
                onChange={(e) => setCourseName(e.target.value)}>
                <option value="">Select a course</option>
                {courses.map((course) => (
                    <option key={course} value={course}>
                        {course}
                    </option>
                ))}
            </select>
            <input
                type="text"
                id="sectionNumber"
                value={sectionNumber}
                onChange={(e) => setSectionNumber(e.target.value)}
                placeholder="Course Number"
                pattern="\d{3}[A-Z]?"
                title="Please enter three digits followed by an optional uppercase letter."
                required
            />
            <button onClick={handleAddCourse}>Add Class</button>
            {errorMessage && <div style={{ color: "red" }}>{errorMessage}</div>}
        </div>
    );
};

export default CourseForm;
