import React, { useState } from "react";

function CourseForm({ onAddCourse, onSubmit }) {
    const [courseName, setCourseName] = useState("");
    const [sectionNumber, setSectionNumber] = useState("");
    const [quarter, setQuarter] = useState("");
    const [minCourses, setMinCourses] = useState(2);
    const [maxCourses, setMaxCourses] = useState(4);
    const [year, setYear] = useState(2024);

    const handleSubmit = (e) => {
        e.preventDefault();
        onAddCourse(courseName, sectionNumber);
        setCourseName("");
        setSectionNumber("");
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        onSubmit({
            courseName,
            sectionNumber,
            quarter,
            minCourses,
            maxCourses,
            year,
        });
    };

    const quarterOptions = [
        { label: "Winter", value: 10 },
        { label: "Spring", value: 20 },
        { label: "Summer", value: 30 },
        { label: "Fall", value: 40 },
    ];

    return (
        <div>
            <header className="navbar">
                <a href="/" className="logo">
                    WWU Schedule Optimizer
                </a>
