import React, { useRef, useEffect } from "react";
import { DayPilot, DayPilotCalendar } from "@daypilot/daypilot-lite-react";
import "./CalendarStyles.css";

const Calendar = ({ schedules }) => {
    const calendarRef = useRef();

    useEffect(() => {
        if (!schedules || schedules.length === 0) {
            return;
        }

        const events = schedules.flatMap((schedule, index) =>
            (schedule.courses || []).map((course) => ({
                id: course.crn,
                text: `${course.subject} (${course.instructor})`,
                start: `2023-10-02T${course.start_time}`,
                end: `2023-10-02T${course.end_time}`,
                backColor: "#6aa84f",
                scheduleIndex: index,
            }))
        );

        const startDate = "2023-10-02"; // Set the appropriate start date

        calendarRef.current.control.update({ startDate, events });
    }, [schedules]);

    return (
        <div className="w-full">
            <DayPilotCalendar
                {...{
                    viewType: "Week",
                    durationBarVisible: false,
                }}
                ref={calendarRef}
            />
        </div>
    );
};

export default Calendar;
