import React, { useState, useEffect } from "react";
import Dropdown from "./Dropdown";

const CourseForm = ({ onAddCourse }) => {
    const [courseName, setCourseName] = useState("");
    const [section, setSection] = useState("");
    const [subjects, setSubjects] = useState([]);

    useEffect(() => {
        fetch("/subjects.txt")
            .then((response) => response.text())
            .then((text) => {
                const courses = text.trim().split("\n");
                setSubjects(courses);
                setCourseName(courses[0]); // Set the default value to the first subject
            })
            .catch((error) => {
                console.error("Error fetching subjects:", error);
            });
    }, []);

    const handleAddCourse = () => {
        if (courseName && onAddCourse(courseName, section)) {
            setCourseName(subjects[0]);
            setSection("");
        }
    };

    return (
        <div>
            <Dropdown
                label="Select Course"
                value={courseName}
                options={subjects}
                onChange={(e) => setCourseName(e.target.value)}
            />
            <input
                type="text"
                value={section}
                placeholder="Course Number"
                onChange={(e) => setSection(e.target.value)}
                className="input input-bordered mt-2 w-full max-w-xs"
            />
            <button
                className="btn btn-primary mt-2 bg-white border-lightgray"
                onClick={handleAddCourse}>
                Add Course
            </button>
        </div>
    );
};

export default CourseForm;
